import React, { useState } from "react";
import nextArrow from "/Users/user/Desktop/hardwork/my-react-app/src/image/next.png";
import prevArrow from "/Users/user/Desktop/hardwork/my-react-app/src/image/back.png";

export default function ImageCard({ imageItem, height, width }) {
  const [currentIndex, setCurrentIndex] = useState(0);

  const handlePrev = () => {
    if (currentIndex > 0) {
      setCurrentIndex(currentIndex - 1);
    } else {
      setCurrentIndex(imageItem.images.length - 1);
    }
  };

  const handleNext = () => {
    if (currentIndex < imageItem.images.length - 1) {
      setCurrentIndex(currentIndex + 1);
    } else {
      setCurrentIndex(0);
    }
  };

  return (
    <div
      style={{
        position: "relative",
        overflow: "hidden",
        width: width,
        height: height,
      }}
    >
      <button
        type="button"
        onClick={handlePrev}
        style={{
          position: "absolute",
          top: "50%",
          left: "0",
          transform: "translateY(-50%)",
          backgroundColor: "transparent",
          border: "none",
          cursor: "pointer",
          zIndex: 10,
        }}
      >
        <img
          src={prevArrow}
          alt="Previous"
          style={{ width: "15px", height: "15px" }}
        />
      </button>
      <button
        type="button"
        onClick={handleNext}
        style={{
          position: "absolute",
          top: "50%",
          right: "0",
          transform: "translateY(-50%)",
          backgroundColor: "transparent",
          border: "none",
          cursor: "pointer",
          zIndex: 10,
        }}
      >
        <img
          src={nextArrow}
          alt="Next"
          style={{ width: "15px", height: "15px" }}
        />
      </button>
      {imageItem.images.map((item, index) => (
        <div
          key={index}
          style={{
            display: currentIndex === index ? "block" : "none",
            width: "100%",
            height: "100%",
          }}
        >
          <img
            src={item.image}
            alt="sample"
            style={{ height: height, width: width }}
          />
        </div>
      ))}
    </div>
  );
}
