import React, { useState } from "react";

function RoomListCard({ data }) {
  const [currentIndex, setCurrentIndex] = useState(1); // 초기 중앙 슬라이드 설정

  const nextSlide = () => {
    setCurrentIndex((prevIndex) =>
      prevIndex === data.length - 1 ? 0 : prevIndex + 1
    );
  };

  const prevSlide = () => {
    setCurrentIndex((prevIndex) =>
      prevIndex === 0 ? data.length - 1 : prevIndex - 1
    );
  };

  return (
    <div className="slider">
      <button onClick={prevSlide} className="arrow prev">
        ←
      </button>
      <div className="slider-wrapper">
        {data.map((room, index) => (
          <div
            key={index}
            className={`slide ${index === currentIndex ? "active" : ""}`}
          >
            <img src={room.image} alt={`room-${index}`} />
            <img src={room.userImage} alt={`user-${index}`} />
          </div>
        ))}
      </div>
      <button onClick={nextSlide} className="arrow next">
        →
      </button>
    </div>
  );
}

export default RoomListCard;
