import React, { useEffect, useState } from "react";
import RoomCard from "./RoomCard";
import "./RoomListCard.css";
import backIcon from "../../image/back.png";
import nextIcon from "../../image/next.png";

export default function RoomListCard({ RoomList }) {
  const len = RoomList.length;
  const [index, setIndex] = useState(0);
  const [isBackDisabled, setIsBackDisabled] = useState(false);
  const [isNextDisabled, setIsNextDisabled] = useState(false);
  const onClickBack = () => {
    setIndex(index - 1);
  };
  const onClickNext = () => {
    setIndex(index + 1);
  };
  useEffect(() => {
    if (index === 0) {
      setIsBackDisabled(true);
    } else {
      setIsBackDisabled(false);
    }
    if (index + 4 >= len) {
      setIsNextDisabled(true);
    } else {
      setIsNextDisabled(false);
    }
  }, [index]);
  return (
    <div style={{ display: "flex", justifyContent: "space-between" }}>
      <button
        onClick={onClickBack}
        style={{ width: "5%", backgroundColor: "white", border: "none" }}
        disabled={isBackDisabled ? true : false}
      >
        <img
          src={backIcon}
          alt="backIcon"
          style={{ width: "20px" }}
          className={isBackDisabled ? "button_off" : "button_on"}
        />
      </button>
      <div className="roomListContainer">
        <div
          className="slideContent"
          style={{ transform: `translateX(-${index * 25}%)` }}
        >
          <RoomCard item={RoomList[index]} />
          <RoomCard item={RoomList[index + 1]} />
          <RoomCard item={RoomList[index + 2]} />
          <RoomCard item={RoomList[index + 3]} />
        </div>
      </div>
      <button
        onClick={onClickNext}
        style={{ width: "5%", backgroundColor: "white", border: "none" }}
        disabled={isNextDisabled ? true : false}
      >
        <img
          src={nextIcon}
          alt="nextIcon"
          style={{ width: "20px" }}
          className={isNextDisabled ? "button_off" : "button_on"}
        />
      </button>
    </div>
  );
}
