import React, { useState } from "react";

function RoomListCard({ data }) {
  const [currentIndex, setCurrentIndex] = useState(0);

  const nextSlide = () => {
    setCurrentIndex((prevIndex) =>
      prevIndex === data.length - 4 ? 0 : prevIndex + 1
    );
  };

  const prevSlide = () => {
    setCurrentIndex((prevIndex) =>
      prevIndex === 0 ? data.length - 4 : prevIndex - 1
    );
  };

  return (
    <div className="slider">
      <button onClick={prevSlide} className="arrow prev">
        ←
      </button>
      <div
        className="slider-wrapper"
        style={{ transform: `translateX(-${currentIndex * (100 / 4)}%)` }}
      >
        {data.map((room, index) => (
          <div key={index} className="slide">
            <img src={room.image} alt={`room-${index}`} />
            <img src={room.userImage} alt={`user-${index}`} />
          </div>
        ))}
      </div>
      <button onClick={nextSlide} className="arrow next">
        →
      </button>
    </div>
  );
}

export default RoomListCard;
