import React, { useState } from "react";
import { FaArrowRight, FaArrowLeft } from "react-icons/fa";
import "./RoomListCard.css";
import nextIcon from "../../image/next.png";
import backIcon from "../../image/back.png";

const RoomListCard = ({ data }) => {
  const [current, setCurrent] = useState(0);
  const length = data.length;

  const nextSlide = () => {
    if (current < length - 4) {
      setCurrent(current + 1);
    }
  };

  const prevSlide = () => {
    if (current > 0) {
      setCurrent(current - 1);
    }
  };

  return (
    <div className="slider">
      {current > 0 && (
        <button
          className="left-arrow"
          onClick={prevSlide}
          style={{ backgroundColor: "white", border: "none", outline: "none" }}
        >
          <img
            src={backIcon}
            alt="back"
            style={{ width: "20px", height: "20px" }}
          />
        </button>
      )}
      {current < length - 4 && (
        <button className="right-arrow" onClick={nextSlide}>
          <img src={nextIcon} alt="next" />
        </button>
      )}
      <div
        className="slides"
        style={{ transform: `translateX(-${current * 25}%)` }}
      >
        {data.map((slide, index) => (
          <div className="slide" key={index}>
            <img src={slide.image} alt="slide image" className="slide-image" />
            <p>{slide.text}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default RoomListCard;
