import React, { useState } from "react";
import "./RoomListCard.css";

const Carousel = ({ slides }) => {
  const [currentSlide, setCurrentSlide] = useState(0);

  const nextSlide = () => {
    if (currentSlide < slides.length - 4) {
      setCurrentSlide(currentSlide + 1);
    }
  };

  const prevSlide = () => {
    if (currentSlide > 0) {
      setCurrentSlide(currentSlide - 1);
    }
  };

  return (
    <div className="carousel-container">
      <button onClick={prevSlide} className="arrow arrow-left">
        ←
      </button>
      <div className="carousel-slides">
        {slides.slice(currentSlide, currentSlide + 4).map((slide, index) => (
          <div key={index} className="slide">
            {slide}
          </div>
        ))}
      </div>
      <button onClick={nextSlide} className="arrow arrow-right">
        →
      </button>
    </div>
  );
};

export default Carousel;
